export declare const dongbak = "\n  \uB3D9\uBC31\uAF43 \uAE40\uC720\uC815\n\n  \uC624\uB298\uB3C4 \uB610 \uC6B0\uB9AC \uC218\uD0C9\uC774 \uB9C9 \uCAD3\uAE30\uC5C8\uB2E4. \uB0B4\uAC00 \uC810\uC2EC\uC744 \uBA39\uACE0 \uB098\uBB34\uB97C \uD558\uB7EC \uAC08 \uC591\uC73C\uB85C \uB098\uC62C \uB54C\uC774\uC5C8\uB2E4. \uC0B0\uC73C\uB85C \uC62C\uB77C\uC11C\uB824\uB2C8\uAE4C \uB4F1\uB4A4\uC5D0\uC11C \uD478\uB4DC\uB4DD \uD478\uB4DC\uB4DD \uD558\uACE0 \uB2ED\uC758 \uD683\uC18C\uB9AC\uAC00 \uC57C\uB2E8\uC774\uB2E4. \uAE5C\uC9DD \uB180\uB77C\uC11C \uACE0\uAC1C\uB97C \uB3CC\uB824 \uBCF4\uB2C8 \uC544\uB2C8\uB098 \uB2E4\uB974\uB7B4 \uB450 \uB188\uC774 \uB610 \uC5BC\uB9AC\uC5C8\uB2E4.\n  \uC810\uC21C\uB124 \uC218\uD0C9(\uB300\uAC15\uC774\uAC00 \uD06C\uACE0 \uB611 \uC624\uC18C\uB9AC\uAC19\uC774 \uC2E4\uD30D\uD558\uAC8C \uC0DD\uAE34 \uB188)\uC774 \uB369\uC800\uB9AC \uC791\uC740 \uC6B0\uB9AC \uC218\uD0C9\uC744 \uD568\uBD80\uB85C \uD574\uB0B4\uB294 \uAC83\uC774\uB2E4. \uADF8\uAC83\uB3C4 \uADF8\uB0E5 \uD574\uB0B4\uB294 \uAC83\uC774 \uC544\uB2C8\uB77C \uD478\uB4DC\uB4DD\uD558\uACE0 \uBA74\uB450\uB97C \uCABC\uACE0 \uBB3C\uB7EC\uC130\uB2E4\uAC00 \uC880 \uC0AC\uC774\uB97C \uB450\uACE0 \uD478\uB4DC\uB4DD\uD558\uACE0 \uBAA8\uAC00\uC9C0\uB97C \uCABC\uC558\uB2E4. \uC774\uB807\uAC8C \uBA4B\uC744 \uBD80\uB824 \uAC00\uBA70 \uC5EC\uC9C0\uC5C6\uC774 \uB2E6\uC544 \uB193\uB294\uB2E4. \uADF8\uB7EC\uBA74 \uC774 \uBABB\uC0DD\uAE34 \uAC83\uC740 \uCABC\uC77C \uC801\uB9C8\uB2E4 \uC8FC\uB465\uC774\uB85C \uB545\uC744 \uBC1B\uC73C\uBA70 \uADF8 \uBE44\uBA85\uC774 \uD0A5, \uD0A5, \uD560\uBFD0\uC774\uB2E4. \uBB3C\uB860 \uBBF8\uCC98 \uC544\uBB3C\uC9C0\uB3C4 \uC54A\uC740 \uBA74\uB450\uB97C \uB610 \uCABC\uC774\uBA70 \uBD89\uC740 \uC120\uD608\uC740 \uB69D\uB69D \uB5A8\uC5B4\uC9C4\uB2E4. \uC774\uAC78 \uAC00\uB9CC\uD788 \uB0B4\uB824\uB2E4\uBCF4\uC790\uB2C8 \uB0B4 \uB300\uAC15\uC774\uAC00 \uD130\uC838\uC11C \uD53C\uAC00 \uD750\uB974\uB294 \uAC83\uAC19\uC774 \uB450\uB208\uC5D0\uC11C \uBD88\uC774 \uBC88\uCA4D \uB09C\uB2E4. \uB300\uB738 \uC9C0\uAC8C \uB9C9\uB300\uAE30\uB97C \uBA54\uACE0 \uB2EC\uB824\uB4E4\uC5B4 \uC810\uC21C\uB124 \uB2ED\uC744 \uD6C4\uB824\uCE60\uAE4C \uD558\uB2E4\uAC00 \uC0DD\uAC01\uC744 \uACE0\uCCD0\uBA39\uACE0 \uD5DB\uB9E4\uC9C8\uB85C \uB5BC\uC5B4\uB9CC \uB193\uC558\uB2E4.\n  \uC774\uBC88\uC5D0\uB3C4 \uC810\uC21C\uC774\uAC00 \uC308\uC744 \uBD99\uC5EC \uB1A8\uC744 \uAC83\uC774\uB2E4. \uBC14\uC9DD\uBC14\uC9DD \uB0B4 \uAE30\uB97C \uC62C\uB9AC\uB290\uB77C\uACE0 \uADF8\uB7AC\uC74C\uC5D0 \uD2C0\uB9BC\uC5C6\uC744 \uAC83\uC774\uB2E4. \uACE0\uB188\uC758 \uACC4\uC9D1\uC560\uAC00 \uC694\uC0C8\uB85C \uB4E4\uC5B4\uC11C \uC65C \uB098\uB97C \uBABB \uBA39\uACA0\uB2E4\uACE0 \uACE0\uB807\uAC8C \uC544\uB974\uB989\uAC70\uB9AC\uB294\uC9C0 \uBAA8\uB978\uB2E4.\n  \uB098\uD758 \uC804 \uAC10\uC790\uAC74\uB9CC \uD558\uB354\uB77C\uB3C4 \uB098\uB294 \uC800\uC5D0\uAC8C \uC870\uAE08\uB3C4 \uC798\uBABB\uD55C \uAC83\uC740 \uC5C6\uB2E4. \uACC4\uC9D1\uC560\uAC00 \uB098\uBB3C\uC744 \uCE90\uB7EC \uAC00\uBA74 \uAC14\uC9C0 \uB0A8 \uC6B8\uD0C0\uB9AC \uC5EE\uB294 \uB370 \uC329\uC774\uC9C8\uC744 \uD558\uB294 \uAC83\uC740 \uB2E4 \uBB50\uB0D0. \uADF8\uAC83\uB3C4 \uBC1C\uC18C\uB9AC\uB97C \uC8FD\uC5EC \uAC00\uC9C0\uACE0 \uB4F1\uB4A4\uB85C \uC0B4\uBA70\uC2DC \uC640\uC11C,\n  \"\uC598! \uB108 \uD63C\uC790\uB9CC \uC77C\uD558\uB2C8?\"\n  \uD558\uACE0 \uAE34\uCE58 \uC54A\uB294 \uC218\uC791\uC744 \uD558\uB294 \uAC83\uC774\uB2E4.\n  \uC5B4\uC81C\uAE4C\uC9C0\uB3C4 \uC800\uC640 \uB098\uB294 \uC774\uC57C\uAE30\uB3C4 \uC798 \uC54A\uACE0 \uC11C\uB85C \uB9CC\uB098\uB3C4 \uBCF8\uCCB4\uB9CC\uCCB4\uACE0 \uC774\uB807\uAC8C \uC810\uC796\uAC8C \uC9C0\uB0B4\uB358 \uD130\uC774\uB828\uB9CC \uC624\uB298\uB85C \uAC11\uC791\uC2A4\uB808 \uB300\uACAC\uD574\uC84C\uC74C\uC740 \uC6EC\uC77C\uC778\uAC00. \uD56D\uCC28 \uB9DD\uC544\uC9C0\uB9CC \uD55C \uACC4\uC9D1\uC560\uAC00 \uB0A8 \uC77C\uD558\uB294 \uB188 \uBCF4\uAD6C\u2026\u2026.\n  \"\uADF8\uB7FC \uD63C\uC790 \uD558\uC9C0 \uB5BC\uB8E8 \uD558\uB4F8?\"\n  \uB0B4\uAC00 \uC774\uB807\uAC8C \uB0B4\uBC30\uC55D\uB294 \uC18C\uB9AC\uB97C \uD558\uB2C8\uAE4C,\n  \"\uB108 \uC77C\uD558\uAE30 \uC88B\uB2C8?\"\n  \uB610\uB294,\n  \"\uD55C\uC5EC\uB984\uC774\uB098 \uB418\uAC70\uB4E0 \uD558\uC9C0 \uBC8C\uC368 \uC6B8\uD0C0\uB9AC\uB97C \uD558\uB2C8?\"\n  \uC794\uC18C\uB9AC\uB97C \uB450\uB8E8 \uB298\uC5B4\uB193\uB2E4\uAC00 \uB0A8\uC774 \uB4E4\uC744\uAE4C\uBD10 \uC190\uC73C\uB85C \uC785\uC744 \uD2C0\uC5B4\uB9C9\uACE0\uB294 \uADF8 \uC18D\uC5D0\uC11C \uAE54\uAE54\uB304\uB2E4. \uBCC4\uB85C \uC6B0\uC2A4\uC6B8 \uAC83\uB3C4 \uC5C6\uB294\uB370 \uB0A0\uC528\uAC00 \uD480\uB9AC\uB354\uB2C8 \uC774 \uB188\uC758 \uACC4\uC9D1\uC560\uAC00 \uBBF8\uCCE4\uB098 \uD558\uACE0 \uC758\uC2EC\uD558\uC600\uB2E4. \uAC8C\uB2E4\uAC00 \uC870\uAE08 \uB4A4\uC5D0\uB294 \uC81C \uC9D1\uAED8\uB97C \uD560\uAE08\uD560\uAE08 \uB3CC\uC544\uBCF4\uB354\uB2C8 \uD589\uC8FC\uCE58\uB9C8\uC758 \uC18D\uC73C\uB85C \uAF08\uB358 \uBC14\uB978\uC190\uC744 \uBF51\uC544\uC11C \uB098\uC758 \uD131\uBC11\uC73C\uB85C \uBD88\uC465 \uB0B4\uBBF8\uB294 \uAC83\uC774\uB2E4. \uC5B8\uC81C \uAD6C\uC6E0\uB294\uC9C0 \uB354\uC6B4 \uAE40\uC774 \uD671 \uB07C\uCE58\uB294 \uAD75\uC740 \uAC10\uC790 \uC138 \uAC1C\uAC00 \uC190\uC5D0 \uBFCC\uB4EF\uC774 \uC950\uC600\uB2E4.\n  \"\uB290 \uC9D1\uC5D4 \uC774\uAC70 \uC5C6\uC9C0?\"\n  \uD558\uACE0 \uC0DD\uC0C9\uC788\uB294 \uD070\uC18C\uB9AC\uB97C \uD558\uACE0\uB294 \uC81C\uAC00 \uC900 \uAC83\uC744 \uB0A8\uC774 \uC54C\uBA74\uC740 \uD070\uC77C\uB0A0\uD14C\uB2C8 \uC5EC\uAE30\uC11C \uC5BC\uB978 \uBA39\uC5B4 \uBC84\uB9AC\uB780\uB2E4. \uADF8\uB9AC\uACE0 \uB610 \uD558\uB294 \uC18C\uB9AC\uAC00,\n  \"\uB108 \uBD04\uAC10\uC790\uAC00 \uB9DB\uC788\uB2E8\uB2E4.\"\n  \"\uB09C \uAC10\uC790 \uC548 \uBA39\uB294\uB2E4. \uB108\uB098 \uBA39\uC5B4\uB77C.\"\n  \uB098\uB294 \uACE0\uAC1C\uB3C4 \uB3CC\uB9AC\uC9C0 \uC54A\uACE0 \uC77C\uD558\uB358 \uC190\uC73C\uB85C \uADF8 \uAC10\uC790\uB97C \uB3C4\uB85C \uC5B4\uAE68 \uB108\uBA38\uB85C \uC465 \uBC00\uC5B4 \uBC84\uB838\uB2E4. \uADF8\uB7AC\uB354\uB2C8 \uADF8\uB798\uB3C4 \uAC00\uB294 \uAE30\uC0C9\uC774 \uC5C6\uACE0, \uBFD0\uB9CC \uC544\uB2C8\uB77C \uC314\uADFC\uC314\uADFC\uD558\uACE0 \uC2EC\uC0C1\uCE58 \uC54A\uAC8C \uC228\uC18C\uB9AC\uAC00 \uC810\uC810 \uAC70\uCE60\uC5B4\uC9C4\uB2E4. \uC774\uAC74 \uB610 \uBB50\uC57C \uC2F6\uC5B4\uC11C \uADF8\uB54C\uC5D0\uC57C \uBE44\uB85C\uC18C \uB3CC\uC544\uB2E4\uBCF4\uB2C8 \uB098\uB294 \uCC38\uC73C\uB85C \uB180\uB790\uB2E4. \uC6B0\uB9AC\uAC00 \uC774 \uB3D9\uB124\uC5D0 \uB4E4\uC5B4\uC628 \uAC83\uC740 \uADFC \uC0BC\uB144\uC9F8 \uB418\uC5B4\uC624\uC9C0\uB9CC \uC5EC\uD0DC\uAECF \uAC00\uBB34\uC7A1\uC7A1\uD55C \uC810\uC21C\uC774\uC758 \uC5BC\uAD74\uC774 \uC774\uB807\uAC8C\uAE4C\uC9C0 \uD64D\uB2F9\uBB34\uCC98\uB7FC \uC0C8\uBE68\uAC1C\uC9C4 \uBC95\uC774 \uC5C6\uC5C8\uB2E4. \uAC8C\uB2E4 \uB208\uC5D0 \uB3C5\uC744 \uC62C\uB9AC\uACE0 \uD55C\uCC38 \uB098\uB97C \uC694\uB807\uAC8C \uC3D8\uC544\uBCF4\uB354\uB2C8 \uB098\uC911\uC5D0\uB294 \uB208\uBB3C\uAE4C\uC9C0 \uC5B4\uB9AC\uB294 \uAC83\uC774 \uC544\uB2C8\uB0D0. \uADF8\uB9AC\uACE0 \uBC14\uAD6C\uB2C8\uB97C \uB2E4\uC2DC \uC9D1\uC5B4\uB4E4\uB354\uB2C8 \uC774\uB97C \uAF2D \uC545\uBB3C\uACE0\uB294 \uC5CE\uC5B4\uC9C8 \uB4EF \uC790\uBE60\uC9C8 \uB4EF \uB17C\uB451\uC73C\uB85C \uD6A1\uD558\uAC8C \uB2EC\uC544\uB098\uB294 \uAC83\uC774\uB2E4.\n  \uC5B4\uCA4C\uB2E4 \uB3D9\uB9AC \uC5B4\uB978\uC774,\n  \"\uB108 \uC5BC\uB978 \uC2DC\uC9D1\uC744 \uAC00\uC57C\uC9C0?\"\n  \uD558\uACE0 \uC6C3\uC73C\uBA74,\n  \"\uC5FC\uB824 \uB9C8\uC11C\uC720. \uAC08 \uB54C \uB418\uBA74 \uC5B4\uB828\uD788 \uAC08\uB77C\uAD6C!\"\n  \uC774\uB807\uAC8C \uCC9C\uC5F0\uB355\uC2A4\uB808 \uBC1B\uB294 \uC810\uC21C\uC774\uC5C8\uB2E4. \uBCF8\uC2DC \uBD80\uB044\uB7FC\uC744 \uD0C0\uB294 \uACC4\uC9D1\uC560\uB3C4 \uC544\uB2C8\uAC70\uB2C8\uC640 \uB610\uD55C \uBD84\uD558\uB2E4\uACE0 \uB208\uC5D0 \uB208\uBB3C\uC744 \uBCF4\uC77C \uC5BC\uBCD1\uC774\uB3C4 \uC544\uB2C8\uB2E4. \uBD84\uD558\uBA74 \uCC28\uB77C\uB9AC \uB098\uC758 \uB4F1\uC5B4\uB9AC\uB97C \uBC14\uAD6C\uB2C8\uB85C \uD55C\uBC88 \uBAA8\uC9C8\uAC8C \uD6C4\uB824\uC314\uB9AC\uACE0 \uB2EC\uC544\uB0A0\uC9C0\uC5B8\uC815.\n  \uADF8\uB7F0\uB370 \uACE0\uC57D\uD55C \uADF8 \uAF34\uC744 \uD558\uACE0 \uAC00\uB354\uB2C8 \uADF8 \uB4A4\uB85C\uB294 \uB098\uB97C \uBCF4\uBA74 \uC7A1\uC544\uBA39\uC73C\uB824 \uAE30\uB97C \uBCF5\uBCF5 \uC4F0\uB294 \uAC83\uC774\uB2E4.\n  \uC124\uD639 \uC8FC\uB294 \uAC10\uC790\uB97C \uC548 \uBC1B\uC544\uBA39\uB294 \uAC83\uC774 \uC2E4\uB840\uB77C \uD558\uBA74, \uC8FC\uBA74 \uADF8\uB0E5 \uC8FC\uC5C8\uC9C0 '\uB290 \uC9D1\uC5D4 \uC774\uAC70 \uC5C6\uC9C0.'\uB294 \uB2E4 \uBB50\uB0D0. \uADF8\uB7EC\uC796\uC544\uB3C4 \uC800\uD76C\uB294 \uB9C8\uB984\uC774\uACE0 \uC6B0\uB9AC\uB294 \uADF8 \uC190\uC5D0\uC11C \uBC30\uC7AC\uB97C \uC5BB\uC5B4 \uB545\uC744 \uBD80\uCE58\uBBC0\uB85C \uC77C\uC0C1 \uAD7D\uC2E4\uAC70\uB9B0\uB2E4. \uC6B0\uB9AC\uAC00 \uC774 \uB9C8\uC744\uC5D0 \uCC98\uC74C \uB4E4\uC5B4\uC640 \uC9D1\uC774 \uC5C6\uC5B4\uC11C \uACE4\uB780\uC73C\uB85C \uC9C0\uB0BC \uC81C \uC9D1\uD130\uB97C \uBE4C\uB9AC\uACE0 \uADF8 \uC704\uC5D0 \uC9D1\uC744 \uB610 \uC9D3\uB3C4\uB85D \uB9C8\uB828\uD574 \uC900 \uAC83\uB3C4 \uC810\uC21C\uB124\uC758 \uD638\uC758\uC600\uB2E4. \uADF8\uB9AC\uACE0 \uC6B0\uB9AC \uC5B4\uBA38\uB2C8 \uC544\uBC84\uC9C0\uB3C4 \uB18D\uC0AC \uB54C \uC591\uC2DD\uC774 \uB538\uB9AC\uBA74 \uC810\uC21C\uC774\uB124\uD55C\uD14C \uAC00\uC11C \uBD80\uC9C0\uB7F0\uD788 \uAFB8\uC5B4\uB2E4 \uBA39\uC73C\uBA74\uC11C \uC778\uD488 \uADF8\uB7F0 \uC9D1\uC740 \uB2E4\uC2DC\uC5C6\uC73C\uB9AC\uB77C\uACE0 \uCE68\uC774 \uB9C8\uB974\uB3C4\uB85D \uCE6D\uCC2C\uD558\uACE4 \uD558\uB294 \uAC83\uC774\uB2E4. \uADF8\uB7EC\uBA74\uC11C\uB3C4 \uC5F4\uC77C\uACF1\uC529\uC774\uB098 \uB41C \uAC83\uB4E4\uC774 \uC218\uAD70\uC218\uAD70\uD558\uACE0 \uBD99\uC5B4 \uB2E4\uB2C8\uBA74 \uB3D9\uB124\uC758 \uC18C\uBB38\uC774 \uC0AC\uB0A9\uB2E4\uACE0 \uC8FC\uC758\uB97C \uC2DC\uCF1C\uC900 \uAC83\uB3C4 \uB610 \uC5B4\uBA38\uB2C8\uC600\uB2E4. \uC65C\uB0D0\uD558\uBA74 \uB0B4\uAC00 \uC810\uC21C\uC774 \uD558\uACE0 \uC77C\uC744 \uC800\uC9C8\uB800\uB2E4\uAC00\uB294 \uC810\uC21C\uB124\uAC00 \uB178\uD560 \uAC83\uC774\uACE0, \uADF8\uB7EC\uBA74 \uC6B0\uB9AC\uB294 \uB545\uB3C4 \uB5A8\uC5B4\uC9C0\uACE0 \uC9D1\uB3C4 \uB0B4\uCAD3\uAE30\uACE0 \uD558\uC9C0 \uC54A\uC73C\uBA74 \uC548\uB418\uB294 \uAE4C\uB2ED\uC774\uC5C8\uB2E4.\n  \uADF8\uB7F0\uB370 \uC774\uB188\uC758 \uACC4\uC9D1\uC560\uAC00 \uAE4C\uB2ED \uC5C6\uC774 \uAE30\uB97C \uBCF5\uBCF5 \uC4F0\uBA70 \uB098\uB97C \uB9D0\uB824 \uC8FD\uC774\uB824\uACE0 \uB4DC\uB294 \uAC83\uC774\uB2E4.\n  \uB208\uBB3C\uC744 \uD758\uB9AC\uACE0 \uAC04 \uB2F4\uB0A0 \uC800\uB141\uB098\uC808\uC774\uC5C8\uB2E4. \uB098\uBB34\uB97C \uD55C \uC9D0 \uC794\uB729 \uC9C0\uACE0 \uC0B0\uC744 \uB0B4\uB824\uC624\uB824\uB2C8\uAE4C \uC5B4\uB514\uC11C \uB2ED\uC774 \uC8FD\uB294\uC18C\uB9AC\uB97C \uCE5C\uB2E4. \uC774\uAC70 \uB258\uC9D1\uC5D0\uC11C \uB2ED\uC744 \uC7A1\uB098, \uD558\uACE0 \uC810\uC21C\uB124 \uC6B8 \uB4A4\uB85C \uB3CC\uC544\uC624\uB2E4\uAC00 \uB098\uB294 \uACE0\uB9CC \uB450 \uB208\uC774 \uB625\uADF8\uB7AC\uB2E4. \uC810\uC21C\uC774\uAC00 \uC800\uD76C \uC9D1 \uBD09\uB2F9\uC5D0 \uD640\uB85C \uAC78\uD130\uC549\uC558\uB294\uB370 \uC774\uAC8C \uCE58\uB9C8 \uC55E\uC5D0\uB2E4 \uC6B0\uB9AC \uC528\uC554\uD0C9\uC744 \uAF2D \uBD99\uB4E4\uC5B4 \uB193\uACE0\uB294,\n  \"\uC774\uB188\uC758 \uC528\uB2ED! \uC8FD\uC5B4\uB77C \uC8FD\uC5B4\uB77C.\"\n  \uC694\uB807\uAC8C \uC554\uD321\uC2A4\uB808 \uD328 \uC8FC\uB294 \uAC83\uC774 \uC544\uB2CC\uAC00. \uADF8\uAC83\uB3C4 \uB300\uAC00\uB9AC\uB098 \uCE58\uBA74 \uBAA8\uB978\uB2E4\uB9C8\uB294 \uC544\uC8FC \uC54C\uB3C4 \uBABB \uB0B3\uC73C\uB77C\uACE0 \uADF8 \uBCFC\uAE30\uC9DD\uAED8\uB97C \uC8FC\uBA39\uC73C\uB85C \uCF55\uCF55 \uC950\uC5B4\uBC15\uB294 \uAC83\uC774\uB2E4.\n  \uB098\uB294 \uB208\uC5D0 \uC30D\uC2EC\uC9C0\uAC00 \uC624\uB974\uACE0 \uC0AC\uC9C0\uAC00 \uBD80\uB974\uB974 \uB5A8\uB838\uC73C\uB098 \uC0AC\uBC29\uC744 \uD55C\uBC88 \uD718\uB458\uB7EC\uBCF4\uACE0\uC57C \uADF8\uC81C\uC11C\uC57C \uC810\uC21C\uC774 \uC9D1\uC5D0 \uC544\uBB34\uB3C4 \uC5C6\uC74C\uC744 \uC54C\uC558\uB2E4. \uC7A1\uC740 \uCC38\uC9C0\uAC8C \uB9C9\uB300\uAE30\uB97C \uB4E4\uC5B4 \uC6B8\uD0C0\uB9AC\uC758 \uC911\uD131\uC744 \uD6C4\uB824\uCE58\uBA70,\n  \"\uC774\uB188\uC758 \uACC4\uC9D1\uC560! \uB0A8\uC758 \uB2ED \uC54C \uBABB \uB0B3\uC73C\uB77C\uAD6C \uADF8\uB7EC\uB2C8?\"\n  \uD558\uACE0 \uC18C\uB9AC\uB97C \uBE7D \uC9C8\uB800\uB2E4.\n  \uADF8\uB7EC\uB098 \uC810\uC21C\uC774\uB294 \uC870\uAE08\uB3C4 \uB180\uB77C\uB294 \uAE30\uC0C9\uC774 \uC5C6\uACE0 \uADF8\uB300\uB85C \uC758\uC813\uC774 \uC549\uC544\uC11C \uC81C \uB2ED \uAC00\uC9C0\uACE0 \uD558\uB4EF\uC774 \uB610 \uC8FD\uC5B4\uB77C, \uC8FD\uC5B4\uB77C, \uD558\uACE0 \uD328\uB294 \uAC83\uC774\uB2E4. \uC774\uAC78 \uBCF4\uBA74 \uB0B4\uAC00 \uC0B0\uC5D0\uC11C \uB0B4\uB824\uC62C \uB54C\uB97C \uACA8\uB0E5\uD574 \uAC00\uC9C0\uACE0 \uBBF8\uB9AC\uBD80\uD130 \uB2ED\uC744 \uC7A1\uC544 \uAC00\uC9C0\uACE0 \uC788\uB2E4\uAC00 \uB124 \uBCF4\uB77C\uB294 \uB4EF\uC774 \uB0B4 \uC55E\uC5D0\uC11C \uC934\uC9C0\uB974\uACE0 \uC788\uC74C\uC774 \uD655\uC2E4\uD558\uB2E4.\n  \uADF8\uB7EC\uB098 \uB098\uB294 \uADF8\uB807\uB2E4\uACE0 \uB0A8\uC758 \uC9D1\uC5D0 \uB6F0\uC5B4\uB4E4\uC5B4\uAC00 \uACC4\uC9D1\uC560\uD558\uACE0 \uC2F8\uC6B8 \uC218\uB3C4 \uC5C6\uB294 \uB178\uB987\uC774\uACE0 \uD615\uD3B8\uC774 \uC369 \uBD88\uB9AC\uD568\uC744 \uC54C\uC558\uB2E4. \uADF8\uB798 \uB2ED\uC774 \uB9DE\uC744 \uC801\uB9C8\uB2E4 \uC9C0\uAC8C \uB9C9\uB300\uAE30\uB85C \uC6B8\uD0C0\uB9AC\uB97C \uD6C4\uD3B4\uCE60 \uC218\uBC16\uC5D0 \uBCC4 \uB3C4\uB9AC\uAC00 \uC5C6\uB2E4. \uC65C\uB0D0\uD558\uBA74 \uC6B8\uD0C0\uB9AC\uB97C \uCE58\uBA74 \uCE60\uC218\uB85D \uC6B8\uC136\uC774 \uBB3C\uB7EC\uC549\uC73C\uBA70 \uBF08\uB300\uB9CC \uB0A8\uAE30 \uB54C\uBB38\uC774\uB2E4. \uD5C8\uB098 \uC544\uBB34\uB9AC \uC0DD\uAC01\uD558\uC5EC\uB3C4 \uB098\uB9CC \uBC11\uC9C0\uB294 \uB178\uB987\uC774\uB2E4.\n  \"\uC544, \uC774\uB144\uC544! \uB0A8\uC758 \uB2ED \uC544\uC8FC \uC8FD\uC77C \uD130\uC774\uC57C?\"\n  \uB0B4\uAC00 \uB3C4\uB07C\uB208\uC744 \uB728\uACE0 \uB2E4\uC2DC \uAF65 \uD638\uB839\uC744 \uD558\uB2C8\uAE4C \uADF8\uC81C\uC11C\uC57C \uC6B8\uD0C0\uB9AC\uAED8\uB85C \uCABC\uB974\uB974 \uC624\uB354\uB2C8 \uC6B8\uBC16\uC5D0 \uC130\uB294 \uB098\uC758 \uBA38\uB9AC\uB97C \uACA8\uB204\uACE0 \uB2ED\uC744 \uB0B4\uD33D\uAC1C\uCE5C\uB2E4.\n  \"\uC608\uC774 \uB354\uB7FD\uB2E4! \uB354\uB7FD\uB2E4!\"\n  \"\uB354\uB7EC\uC6B4 \uAC78 \uB110\uB354\uB7EC \uC785\uB54C \uB07C\uACE0 \uC788\uC73C\uB7AC\uB2C8? \uB9DD\uD560 \uACC4\uC9D1\uC560\uB144 \uAC19\uC73C\uB2C8\"\n  \uD558\uACE0 \uB098\uB3C4 \uB354\uB7FD\uB2E8 \uB4EF\uC774 \uC6B8\uD0C0\uB9AC\uAED8\uB97C \uD6A1\uD5C8\uCF00 \uB3CC\uC544\uB0B4\uB9AC\uBA70 \uC57D\uC774 \uC624\uB97C \uB300\uB85C \uB2E4 \uC62C\uB790\uB2E4, \uB77C\uACE0 \uD558\uB294 \uAC83\uC740 \uC554\uD0C9\uC774 \uD48D\uAE30\uB294 \uC11C\uC2AC\uC5D0 \uB098\uC758 \uC774\uB9C8\uBE7C\uAE30\uC5D0\uB2E4 \uBB3C\uC9C0\uB625\uC744 \uCC0D \uAC08\uACBC\uB294\uB370 \uADF8\uAC78 \uBCF8\uB2E4\uBA74 \uC54C\uC9D1\uB9CC \uD130\uC84C\uC744 \uBFD0 \uC544\uB2C8\uB77C \uACE8\uBCD1\uC740 \uB2E8\uB2E8\uD788 \uB4E0 \uB4EF\uC2F6\uB2E4. \uADF8\uB9AC\uACE0 \uB098\uC758 \uB4F1\uB4A4\uB97C \uD5A5\uD558\uC5EC \uB098\uC5D0\uAC8C\uB9CC \uB4E4\uB9B4 \uB4EF \uB9D0 \uB4EF\uD55C \uC74C\uC131\uC73C\uB85C,\n  \"\uC774 \uBC14\uBCF4 \uB140\uC11D\uC544!\"\n  \"\uC560! \uB108 \uBC30\uB0C7\uBCD1\uC2E0\uC774\uC9C0?\"\n  \uADF8\uB9CC\uB3C4 \uC88B\uC73C\uB828\uB9CC,\n  \"\uC598! \uB108 \uB290 \uC544\uBC84\uC9C0\uAC00 \uACE0\uC790\uB77C\uC9C0?\"\n  \"\uBB50 \uC6B8 \uC544\uBC84\uC9C0\uAC00 \uADF8\uB798 \uACE0\uC790\uC57C?\"\n  \uD560 \uC591\uC73C\uB85C \uC5F4\uBC99\uAC70\uC9C0\uAC00 \uB098\uC11C \uACE0\uAC1C\uB97C \uD671 \uB3CC\uB9AC\uC5B4 \uBC14\uB77C\uBD24\uB354\uB2C8 \uADF8\uB54C\uAE4C\uC9C0 \uC6B8\uD0C0\uB9AC \uC704\uB85C \uB098\uC640 \uC788\uC5B4\uC57C \uD560 \uC810\uC21C\uC774\uC758 \uB300\uAC00\uB9AC\uAC00 \uC5B4\uB514 \uAC14\uB294\uC9C0 \uBCF4\uC774\uC9C0\uB97C \uC54A\uB294\uB2E4. \uADF8\uB7EC\uB2E4 \uB3CC\uC544\uC11C\uC11C \uC624\uC790\uBA74 \uC544\uAE4C\uC5D0 \uD55C \uC695\uC744 \uC6B8 \uBC16\uC73C\uB85C \uB610 \uD37C\uBD93\uB294 \uAC83\uC774\uB2E4. \uC695\uC744 \uC774\uD1A0\uB85D \uBA39\uC5B4 \uAC00\uBA74\uC11C\uB3C4 \uB300\uAC70\uB9AC \uD55C \uB9C8\uB514 \uBABB\uD558\uB294 \uAC78 \uC0DD\uAC01\uD558\uB2C8 \uB3CC\uBD80\uB9AC\uC5D0 \uCC44\uC774\uC5B4 \uBC1C\uD1B1 \uBC11\uC774 \uD130\uC9C0\uB294 \uAC83\uB3C4 \uBAA8\uB97C \uB9CC\uD07C \uBD84\uD558\uACE0 \uAE09\uAE30\uC57C\uB294 \uB450\uB208\uC5D0 \uB208\uBB3C\uAE4C\uC9C0 \uBD88\uB048 \uB0B4\uC19F\uB294\uB2E4.\n  \uADF8\uB7EC\uB098 \uC810\uC21C\uC774\uC758 \uCE68\uD574\uB294 \uC774\uAC83\uBFD0\uC774 \uC544\uB2C8\uB2E4.\n  \uC0AC\uB78C\uB4E4\uC774 \uC5C6\uC73C\uBA74 \uD2C8\uD2C8\uC774 \uC81C \uC9D1 \uC218\uD0C9\uC744 \uBAB0\uACE0 \uC640\uC11C \uC6B0\uB9AC \uC218\uD0C9\uACFC \uC308\uC744 \uBD99\uC5EC \uB193\uB294\uB2E4. \uC81C \uC9D1 \uC218\uD0C9\uC740 \uC369 \uD5D8\uC0C1\uAD82\uAC8C \uC0DD\uAE30\uACE0 \uC308\uC774\uB77C\uBA74 \uD670\uB97C \uCE58\uB294 \uACE0\uB85C \uC73C\uB808 \uC774\uAE38 \uAC83\uC744 \uC54C\uAE30 \uB54C\uBB38\uC774\uB2E4. \uADF8\uB798\uC11C \uD22D\uD558\uBA74 \uC6B0\uB9AC \uC218\uD0C9\uC774 \uBA74\uB450\uBA70 \uB208\uAE54\uC774 \uD53C\uB85C \uD750\uB4DC\uB974\uD558\uAC8C \uB418\uB3C4\uB85D \uD574 \uB193\uB294\uB2E4. \uC5B4\uB5A4 \uB54C\uC5D0\uB294 \uC6B0\uB9AC \uC218\uD0C9\uC774 \uB098\uC624\uC9C0\uB97C \uC54A\uC73C\uB2C8\uAE4C \uC694\uB188\uC758 \uACC4\uC9D1\uC560\uAC00 \uBAA8\uC774\uB97C \uC950\uACE0 \uC640\uC11C \uAF80\uC5B4\uB0B4\uB2E4\uAC00 \uC308\uC744 \uBD99\uC778\uB2E4.\n  \uC774\uB807\uAC8C \uB418\uBA74 \uB098\uB3C4 \uB2E4\uB978 \uBC30\uCC28\uB97C \uCC28\uB9AC\uC9C0 \uC54A\uC744 \uC218 \uC5C6\uC5C8\uB2E4. \uD558\uB8E8\uB294 \uC6B0\uB9AC \uC218\uD0C9\uC744 \uBD99\uB4E4\uC5B4 \uAC00\uC9C0\uACE0 \uB10C\uC9C0\uC2DC \uC7A5\uB3C5\uAED8\uB85C \uAC14\uB2E4. \uC308\uB2ED\uC5D0\uAC8C \uACE0\uCD94\uC7A5\uC744 \uBA39\uC774\uBA74 \uBCD1\uB4E0 \uD669\uC18C\uAC00 \uC0B4\uBAA8\uC0AC\uB97C \uBA39\uACE0 \uC6A9\uC744 \uC4F0\uB294 \uAC83\uCC98\uB7FC \uAE30\uC6B4\uC774 \uBED7\uCE5C\uB2E4 \uD55C\uB2E4. \uC7A5\uB3C5\uC5D0\uC11C \uACE0\uCD94\uC7A5 \uD55C \uC811\uC2DC\uB97C \uB5A0\uC11C \uB2ED \uC8FC\uB465\uC544\uB9AC\uAED8\uB85C \uB4E4\uC5EC \uBC00\uACE0 \uBA39\uC5EC \uBCF4\uC558\uB2E4. \uB2ED\uB3C4 \uACE0\uCD94\uC7A5\uC5D0 \uB9DB\uC744 \uB4E4\uC600\uB294\uC9C0 \uAC70\uC2A4\uB974\uC9C0 \uC54A\uACE0 \uAC70\uC9C4 \uBC18 \uC811\uC2DC \uD131\uC774\uB098 \uACE7\uC798 \uBA39\uB294\uB2E4. \uADF8\uB9AC\uACE0 \uBA39\uACE0 \uAE08\uC2DC\uB294 \uC6A9\uC744 \uBABB\uC4F8 \uD130\uC774\uBBC0\uB85C \uC5BC\uB9C8\uCBE4 \uAE30\uC6B4\uC774 \uB3CC\uB3C4\uB85D \uD683\uC18D\uC5D0\uB2E4 \uAC00\uB450\uC5B4\uB450\uC5C8\uB2E4.\n  \uBC2D\uC5D0 \uB450\uC5C4\uC744 \uB450\uC5B4 \uC9D0 \uC838\uB0B4\uACE0 \uB098\uC11C \uC274 \uCC38\uC5D0 \uADF8 \uB2ED\uC744 \uC548\uACE0 \uBC16\uC73C\uB85C \uB098\uC654\uB2E4. \uB9C8\uCE68 \uBC16\uC5D0\uB294 \uC544\uBB34\uB3C4 \uC5C6\uACE0 \uC810\uC21C\uC774\uB9CC \uC800\uD76C \uC6B8\uC548\uC5D0\uC11C \uD5CC\uC637\uC744 \uB72F\uB294\uC9C0 \uD639\uC740 \uC19C\uC744 \uD130\uB294\uC9C0 \uC6C5\uD06C\uB9AC\uACE0 \uC549\uC544\uC11C \uC77C\uC744 \uD560 \uBFD0\uC774\uB2E4.\n  \uB098\uB294 \uC810\uC21C\uB124 \uC218\uD0C9\uC774 \uB178\uB294 \uBC2D\uC73C\uB85C \uAC00\uC11C \uB2ED\uC744 \uB0B4\uB824 \uB193\uACE0 \uAC00\uB9CC\uD788 \uB9E5\uC744 \uBCF4\uC558\uB2E4. \uB450 \uB2ED\uC740 \uC5EC\uC804\uD788 \uC5BC\uB9AC\uC5B4 \uC308\uC744 \uD558\uB294\uB370 \uCC98\uC74C\uC5D0\uB294 \uC544\uBB34 \uBCF4\uB78C\uC774 \uC5C6\uC5C8\uB2E4. \uBA4B\uC9C0\uAC8C \uCABC\uB294 \uBC14\uB78C\uC5D0 \uC6B0\uB9AC \uB2ED\uC740 \uB610 \uD53C\uB97C \uD758\uB9AC\uACE0 \uADF8\uB7EC\uBA74\uC11C\uB3C4 \uB0A0\uAC2F\uC8FD\uC9C0\uB9CC \uD478\uB4DC\uB4DD\uD478\uB4DC\uB4DD\uD558\uACE0 \uC62C\uB77C \uB6F0\uACE0 \uB6F0\uACE0 \uD560 \uBFD0\uC73C\uB85C \uC81C\uBC95 \uD55C\uBC88 \uCABC\uC544 \uBCF4\uC9C0\uB3C4 \uBABB\uD55C\uB2E4.\n  \uADF8\uB7EC\uB098 \uD55C\uBC88\uC5D4 \uC5B4\uCA50 \uC77C\uC778\uC9C0 \uC6A9\uC744 \uC4F0\uACE0 \uD384\uCA4D \uB6F0\uB354\uB2C8 \uBC1C\uD1B1\uC73C\uB85C \uB208\uC744 \uD558\uBE44\uACE0 \uB0B4\uB824\uC624\uBA70 \uBA74\uB450\uB97C \uCABC\uC558\uB2E4. \uD070\uB2ED\uB3C4 \uC5EC\uAE30\uC5D0\uB294 \uB180\uB790\uB294\uC9C0 \uB4A4\uB85C \uBA48\uC530\uD558\uBA70 \uBB3C\uB7EC\uB09C\uB2E4. \uC774 \uAE30\uD68C\uB97C \uD0C0\uC11C \uC791\uC740 \uC6B0\uB9AC \uC218\uD0C9\uC774 \uB610 \uB0A0\uC314\uAC8C \uB364\uBCBC\uB4E4\uC5B4 \uB2E4\uC2DC \uBA74\uB450\uB97C \uCABC\uB2C8 \uADF8\uC81C\uC11C\uB294 \uAC10\uB54C\uC0AC\uB098\uC6B4 \uADF8 \uB300\uAC15\uC774\uC5D0\uC11C\uB3C4 \uD53C\uAC00 \uD750\uB974\uC9C0 \uC54A\uC744 \uC218 \uC5C6\uB2E4.\n  \uC633\uB2E4 \uC54C\uC558\uB2E4, \uACE0\uCD94\uC7A5\uB9CC \uBA39\uC774\uBA70\uB294 \uB418\uB294\uAD6C\uB098 \uD558\uACE0 \uB098\uB294 \uC18D\uC73C\uB85C \uC544\uC8FC \uC7C1\uADF8\uB7EC\uC6CC \uC8FD\uACA0\uB2E4. \uADF8\uB54C\uC5D0\uB294 \uB73B\uBC16\uC5D0 \uB0B4\uAC00 \uB2ED\uC308\uC744 \uBD99\uC5EC \uB193\uB294 \uB370 \uB180\uB77C\uC11C \uC6B8 \uBC16\uC73C\uB85C \uB0B4\uB2E4\uBCF4\uACE0 \uC130\uB358 \uC810\uC21C\uC774\uB3C4 \uC785\uB9DB\uC774 \uC4F4\uC9C0 \uB208\uC300\uC744 \uCC0C\uD478\uB838\uB2E4.\n  \uB098\uB294 \uB450 \uC190\uC73C\uB85C \uBCFC\uAE30\uC9DD\uC744 \uB450\uB4DC\uB9AC\uBA70 \uC5F0\uBC29,\n  \"\uC798\uD55C\uB2E4! \uC798\uD55C\uB2E4!\"\uD558\uACE0, \uC2E0\uC774 \uBA38\uB9AC\uB05D\uAE4C\uC9C0 \uBED7\uCE58\uC5C8\uB2E4.\n  \uADF8\uB7EC\uB098 \uC5BC\uB9C8 \uB418\uC9C0 \uC54A\uC544\uC11C \uB098\uB294 \uB10B\uC774 \uD480\uB9AC\uC5B4 \uAE30\uB465\uAC19\uC774 \uBB35\uBB35\uD788 \uC11C \uC788\uAC8C \uB418\uC5C8\uB2E4. \uC65C\uB0D0\uD558\uBA74 \uD070 \uB2ED\uC774 \uD55C\uBC88 \uCABC\uC778 \uC559\uAC1A\uC74C\uC73C\uB85C \uD638\uB4E4\uAC11\uC2A4\uB808 \uC5F0\uAC70\uD478 \uCABC\uB294 \uC11C\uC2AC\uC5D0 \uC6B0\uB9AC \uC218\uD0C9\uC740 \uCC14\uB054 \uBABB\uD558\uACE0 \uB9C9 \uACEF\uB294\uB2E4. \uC774\uAC78 \uBCF4\uACE0\uC11C \uC774\uBC88\uC5D0\uB294 \uC810\uC21C\uC774\uAC00 \uAE54\uAE54\uAC70\uB9AC\uACE0 \uB418\uB3C4\uB85D \uC774\uCABD\uC5D0\uC11C \uB9CE\uC774 \uB4E4\uC73C\uB77C\uACE0 \uC6C3\uB294 \uAC83\uC774\uB2E4.\n  \uB098\uB294 \uBCF4\uB2E4 \uBABB\uD558\uC5EC \uB364\uBCBC\uB4E4\uC5B4\uC11C \uC6B0\uB9AC \uC218\uD0C9\uC744 \uBD99\uB4E4\uC5B4 \uAC00\uC9C0\uACE0 \uB3C4\uB85C \uC9D1\uC73C\uB85C \uB4E4\uC5B4\uC654\uB2E4. \uACE0\uCD94\uC7A5\uC744 \uC880\uB354 \uBA39\uC600\uB354\uB77C\uBA74 \uC88B\uC558\uC744 \uAC78, \uB108\uBB34 \uAE09\uD558\uAC8C \uC308\uC744 \uBD99\uC778 \uAC83\uC774 \uD37D \uD6C4\uD68C\uAC00 \uB09C\uB2E4. \uC7A5\uB3C5\uAED8\uB85C \uB3CC\uC544\uC640\uC11C \uB2E4\uC2DC \uD131\uBC11\uC5D0 \uACE0\uCD94\uC7A5\uC744 \uB4E4\uC774\uB314\uB2E4. \uD765\uBD84\uC73C\uB85C \uB9D0\uBBF8\uC554\uC544 \uADF8\uB7F0\uC9C0 \uB2F9\uCD5C \uBA39\uC9C8 \uC54A\uB294\uB2E4.\n  \uB098\uB294 \uD558\uB9B4\uC5C6\uC774 \uB2ED\uC744 \uBC18\uB4EF\uC774 \uB215\uD788\uACE0 \uADF8 \uC785\uC5D0\uB2E4 \uAD90\uB828 \uBB3C\uBD80\uB9AC\uB97C \uBB3C\uB9AC\uC5C8\uB2E4. \uADF8\uB9AC\uACE0 \uACE0\uCD94\uC7A5 \uBB3C\uC744 \uD0C0\uC11C \uADF8 \uAD6C\uBA4D\uC73C\uB85C \uC870\uAE08\uC529 \uB4E4\uC5EC \uBD80\uC5C8\uB2E4. \uB2ED\uC740 \uC880 \uAD34\uB85C\uC6B4\uC9C0 \uD0A5\uD0A5\uD558\uACE0 \uC7AC\uCC44\uAE30\uB97C \uD558\uB294 \uBAA8\uC591\uC774\uB098 \uADF8\uB7EC\uB098 \uB2F9\uC7A5\uC758 \uAD34\uB85C\uC6C0\uC740 \uB9E4\uC77C \uAC19\uC774 \uD53C\uB97C \uD758\uB9AC\uB294 \uB370 \uB308 \uAC8C \uC544\uB2C8\uB77C \uC0DD\uAC01\uD558\uC600\uB2E4.\n  \uADF8\uB7EC\uB098 \uD55C \uB450\uC5B4 \uC885\uC9C0 \uAC00\uB7C9 \uACE0\uCD94\uC7A5 \uBB3C \uBA39\uC774\uACE0 \uB098\uC11C\uB294 \uB098\uB294 \uACE0\uB9CC \uD480\uC774 \uC8FD\uC5C8\uB2E4. \uC2F1\uC2F1\uD558\uB358 \uB2ED\uC774 \uC65C \uADF8\uB7F0\uC9C0 \uACE0\uAC1C\uB97C \uC0B4\uBA70\uC2DC \uB4A4\uD2C0\uACE0\uB294 \uC190\uC544\uADC0\uC5D0\uC11C \uBED0\uB4DC\uB7EC\uC9C0\uB294 \uAC83\uC774 \uC544\uB2CC\uAC00. \uC544\uBC84\uC9C0\uAC00 \uBCFC\uAE4C \uBD10\uC11C \uC5BC\uB978 \uD670\uC5D0\uB2E4 \uAC10\uCD94\uC5B4 \uB450\uC5C8\uB354\uB2C8 \uC624\uB298 \uC544\uCE68\uC5D0\uC11C\uC57C \uACA8\uC6B0 \uC815\uC2E0\uC774 \uB4E0 \uBAA8\uC591 \uAC19\uB2E4.\n  \uADF8\uB7AC\uB358 \uAC78 \uC774\uB807\uAC8C \uC624\uB2E4 \uBCF4\uB2C8\uAE4C \uB610 \uC308\uC744 \uBD99\uC5EC \uB193\uC73C\uB2C8 \uC774 \uB9DD\uD55C \uACC4\uC9D1\uC560\uAC00 \uD544\uC5F0 \uC6B0\uB9AC \uC9D1\uC5D0 \uC544\uBB34\uB3C4 \uC5C6\uB294 \uD2C8\uC744 \uD0C0\uC11C \uC81C\uAC00 \uB4E4\uC5B4\uC640 \uD670\uC5D0\uC11C \uAEBC\uB0B4 \uAC00\uC9C0\uACE0 \uB098\uAC04 \uAC83\uC774 \uBD84\uBA85\uD558\uB2E4.\n  \uB098\uB294 \uB2E4\uC2DC \uB2ED\uC744 \uC7A1\uC544\uB2E4 \uAC00\uB450\uACE0 \uC5FC\uB824\uB294 \uC2A4\uB7EC\uC6B0\uB098 \uADF8\uB807\uB2E4\uACE0 \uC0B0\uC73C\uB85C \uB098\uBB34\uB97C \uD558\uB7EC \uAC00\uC9C0 \uC54A\uC744 \uC218\uB3C4 \uC5C6\uB294 \uD615\uD3B8\uC774\uC5C8\uB2E4.\n  \uC18C\uB098\uBB34 \uC0AD\uC815\uC774\uB97C \uB530\uBA70 \uAC00\uB9CC\uD788 \uC0DD\uAC01\uD574 \uBCF4\uB2C8 \uC554\uB9CC\uD574\uB3C4 \uACE0\uB144\uC758 \uBAA9\uC7C1\uC774\uB97C \uB3CC\uB824 \uB193\uACE0 \uC2F6\uB2E4. \uC774\uBC88\uC5D0 \uB0B4\uB824\uAC00\uBA74 \uB9DD\uD560 \uB144 \uB4F1\uC904\uAE30\uB97C \uD55C\uBC88 \uB418\uAC8C \uD6C4\uB824\uCE58\uACA0\uB2E4 \uD558\uACE0 \uC2F1\uB465\uAC85\uB465 \uB098\uBB34\uB97C \uC9C0\uACE0\uB294 \uBD80\uB9AC\uB098\uCF00 \uB0B4\uB824\uC654\uB2E4.\n  \uAC70\uC9C0\uBC18 \uC9D1\uC5D0 \uB2E4 \uB0B4\uB824\uC640\uC11C \uB098\uB294 \uD638\uB4DC\uAE30 \uC18C\uB9AC\uB97C \uB4E3\uACE0 \uBC1C\uC774 \uB531 \uBA48\uCD94\uC5C8\uB2E4. \uC0B0\uAE30\uC2AD\uC5D0 \uB110\uB824 \uC788\uB294 \uAD75\uC740 \uBC14\uC717\uB3CC \uD2C8\uC5D0 \uB178\uB780 \uB3D9\uBC31\uAF43\uC774 \uC18C\uBCF4\uB85D\uD558\uB2C8 \uAE54\uB9AC\uC5C8\uB2E4. \uADF8 \uD2C8\uC5D0 \uB07C\uC5B4 \uC549\uC544\uC11C \uC810\uC21C\uC774\uAC00 \uCCAD\uC2B9\uB9DE\uAC8C\uC2DC\uB9AC \uD638\uB4DC\uAE30\uB97C \uBD88\uACE0 \uC788\uB294 \uAC83\uC774\uB2E4. \uADF8\uBCF4\uB2E4\uB3C4 \uB354 \uB180\uB780 \uAC83\uC740 \uACE0 \uC55E\uC5D0\uC11C \uB610 \uD478\uB4DC\uB4DD, \uD478\uB4DC\uB4DD, \uD558\uACE0 \uB4E4\uB9AC\uB294 \uB2ED\uC758 \uD683\uC18C\uB9AC\uB2E4. \uD544\uC5F0\uCF54 \uC694\uB144\uC774 \uB098\uC758 \uC57D\uC744 \uC62C\uB9AC\uB290\uB77C\uACE0 \uB610 \uB2ED\uC744 \uC9D1\uC5B4\uB0B4\uB2E4\uAC00 \uB0B4\uAC00 \uB0B4\uB824\uC62C \uAE38\uBAA9\uC5D0\uB2E4 \uC308\uC744 \uC2DC\uCF1C \uB193\uACE0 \uC800\uB294 \uADF8 \uC55E\uC5D0 \uC549\uC544\uC11C \uCC9C\uC5F0\uC2A4\uB808 \uD638\uB4DC\uAE30\uB97C \uBD88\uACE0 \uC788\uC74C\uC5D0 \uD2C0\uB9BC\uC5C6\uC73C\uB9AC\uB77C.\n  \uB098\uB294 \uC57D\uC774 \uC624\uB97C \uB300\uB85C \uC62C\uB77C\uC11C \uB450 \uB208\uC5D0\uC11C \uBD88\uACFC \uD568\uAED8 \uB208\uBB3C\uC774 \uD37D \uC3DF\uC544\uC84C\uB2E4. \uB098\uBB47\uC9C0\uAC8C\uB3C4 \uBC97\uC5B4 \uB180 \uC0C8 \uC5C6\uC774 \uADF8\uB300\uB85C \uB0B4\uB3D9\uB315\uC774\uCE58\uACE0\uB294 \uC9C0\uAC8C \uB9C9\uB300\uAE30\uB97C \uBED7\uCE58\uACE0 \uD5C8\uB465 \uD5C8\uB465 \uB2EC\uB824\uB4E4\uC5C8\uB2E4.\n  \uAC00\uAE4C\uC774 \uC640 \uBCF4\uB2C8 \uACFC\uC5F0 \uB098\uC758 \uC9D0\uC791\uB300\uB85C \uC6B0\uB9AC \uC218\uD0C9\uC774 \uD53C\uB97C \uD758\uB9AC\uACE0 \uAC70\uC758 \uBE48\uC0AC \uC9C0\uACBD\uC5D0 \uC774\uB974\uB800\uB2E4. \uB2ED\uB3C4 \uB2ED\uC774\uB824\uB2C8\uC640 \uADF8\uB7EC\uD568\uC5D0\uB3C4 \uBD88\uAD6C\uD558\uACE0 \uB208 \uD558\uB098 \uAE5C\uC9DD \uC5C6\uC774 \uACE0\uB300\uB85C \uC549\uC544\uC11C \uD638\uB4DC\uAE30\uB9CC \uBD80\uB294 \uADF8 \uAF34\uC5D0 \uB354\uC6B1 \uCE58\uAC00 \uB5A8\uB9B0\uB2E4. \uB3D9\uB124\uC5D0\uC11C\uB3C4 \uC18C\uBB38\uC774 \uB0AC\uAC70\uB2C8\uC640 \uB098\uB3C4 \uD55C\uB54C\uB294 \uAC71\uC2E4\uAC71\uC2E4\uD788 \uC77C \uC798 \uD558\uACE0 \uC5BC\uAD74 \uC608\uC05C \uACC4\uC9D1\uC560\uC778 \uC904 \uC54C\uC558\uB354\uB2C8 \uC2DC\uBC29 \uBCF4\uB2C8\uAE4C \uADF8 \uB208\uAE54\uC774 \uAF2D \uC5EC\uC6B0 \uC0C8\uB07C \uAC19\uB2E4.\n  \uB098\uB294 \uB300\uB738 \uB2EC\uB824\uB4E4\uC5B4\uC11C \uB098\uB3C4 \uBAA8\uB974\uB294 \uC0AC\uC774\uC5D0 \uD070 \uC218\uD0C9\uC744 \uB2E8\uB9E4\uB85C \uB54C\uB824 \uC5CE\uC5C8\uB2E4. \uB2ED\uC740 \uD479 \uC5CE\uC5B4\uC9C4 \uCC44 \uB2E4\uB9AC \uD558\uB098 \uAF3C\uC9DD \uBABB \uD558\uACE0 \uADF8\uB300\uB85C \uC8FD\uC5B4 \uBC84\uB838\uB2E4. \uADF8\uB9AC\uACE0 \uB098\uB294 \uBA4D\uD558\uB2C8 \uC130\uB2E4\uAC00 \uC810\uC21C\uC774\uAC00 \uB9E4\uC12D\uAC8C \uB208\uC744 \uD649\uB728\uACE0 \uB2E5\uCE58\uB294 \uBC14\uB78C\uC5D0 \uB4A4\uB85C \uBC8C\uB801 \uB098\uC790\uBE60\uC84C\uB2E4.\n  \"\uC774\uB188\uC544! \uB108 \uC65C \uB0A8\uC758 \uB2ED\uC744 \uB54C\uB824\uC8FD\uC774\uB2C8?\"\n  \"\uADF8\uB7FC \uC5B4\uB54C?\"\n  \uD558\uACE0 \uC77C\uC5B4\uB098\uB2E4\uAC00,\n  \"\uBB50 \uC774 \uC790\uC2DD\uC544! \uB204 \uC9D1 \uB2ED\uC778\uB370?\"\n  \uD558\uACE0 \uBCF5\uC7A5\uC744 \uB5BC\uBBF8\uB294 \uBC14\uB78C\uC5D0 \uB2E4\uC2DC \uBC8C\uB801 \uC790\uBE60\uC84C\uB2E4. \uADF8\uB9AC\uACE0 \uB098\uC11C \uAC00\uB9CC\uD788 \uC0DD\uAC01\uC744 \uD558\uB2C8 \uBD84\uD558\uAE30\uB3C4 \uD558\uACE0 \uBB34\uC548\uB3C4\uC2A4\uB7FD\uACE0, \uB610 \uD55C\uD3B8 \uC77C\uC744 \uC800\uC9C8\uB800\uC73C\uB2C8, \uC778\uC820 \uB545\uC774 \uB5A8\uC5B4\uC9C0\uACE0 \uC9D1\uB3C4 \uB0B4\uCAD3\uAE30\uACE0 \uD574\uC57C \uB420\uB294\uC9C0 \uBAA8\uB978\uB2E4.\n  \uB098\uB294 \uBE44\uC2AC\uBE44\uC2AC \uC77C\uC5B4\uB098\uBA70 \uC18C\uB9F7\uC790\uB77D\uC73C\uB85C \uB208\uC744 \uAC00\uB9AC\uACE0\uB294, \uC5BC\uAE40\uC5D0 \uC5C9 \uD558\uACE0 \uC6B8\uC74C\uC744 \uB193\uC558\uB2E4. \uADF8\uB7EC\uB098 \uC810\uC21C\uC774\uAC00 \uC55E\uC73C\uB85C \uB2E4\uAC00\uC640\uC11C,\n  \"\uADF8\uB7FC \uB108 \uC774\uB2F4\uBD80\uD140 \uC548 \uADF8\uB7F4 \uD14C\uB0D0?\"\n  \uD558\uACE0 \uBB3C\uC744 \uB54C\uC5D0\uC57C \uBE44\uB85C\uC18C \uC0B4\uAE38\uC744 \uCC3E\uC740 \uB4EF\uC2F6\uC5C8\uB2E4. \uB098\uB294 \uB208\uBB3C\uC744 \uC6B0\uC120 \uC53B\uACE0 \uBB58 \uC548 \uADF8\uB7EC\uB294\uC9C0 \uBA85\uC0C9\uB3C4 \uBAA8\uB974\uAC74\uB9CC,\n  \"\uADF8\uB798!\"\n  \uD558\uACE0 \uBB34\uD131\uB300\uACE0 \uB300\uB2F5\uD558\uC600\uB2E4.\n  \"\uC694\uB2F4\uBD80\uD130 \uB610 \uADF8\uB798 \uBD10\uB77C, \uB0B4 \uC790\uAFB8 \uBABB\uC0B4\uAC8C \uAD74 \uD14C\uB2C8.\"\n  \"\uADF8\uB798 \uADF8\uB798 \uC774\uC820 \uC548 \uADF8\uB7F4 \uD14C\uC57C!\"\n  \"\uB2ED \uC8FD\uC740 \uAC74 \uC5FC\uB824 \uB9C8\uB77C, \uB0B4 \uC548 \uC774\uB97C \uD14C\uB2C8.\"\n  \uADF8\uB9AC\uACE0 \uBB63\uC5D0 \uB5A0\uB2E4 \uBC00\uB838\uB294\uC9C0 \uB098\uC758 \uC5B4\uAE68\uB97C \uC9DA\uC740 \uCC44 \uADF8\uB300\uB85C \uD37D \uC4F0\uB7EC\uC9C4\uB2E4. \uADF8 \uBC14\uB78C\uC5D0 \uB098\uC758 \uBAB8\uB6B1\uC774\uB3C4 \uACB9\uCCD0\uC11C \uC4F0\uB7EC\uC9C0\uBA70, \uD55C\uCC3D \uD53C\uC5B4 \uD37C\uB4DC\uB7EC\uC9C4 \uB178\uB780 \uB3D9\uBC31\uAF43 \uC18D\uC73C\uB85C \uD3ED \uD30C\uBB3B\uD600 \uBC84\uB838\uB2E4.\n  \uC54C\uC2F8\uD55C, \uADF8\uB9AC\uACE0 \uD5A5\uAE0B\uD55C \uADF8 \uB0C4\uC0C8\uC5D0 \uB098\uB294 \uB545\uC774 \uAEBC\uC9C0\uB294 \uB4EF\uC774 \uC628 \uC815\uC2E0\uC774 \uACE0\uB9CC \uC544\uCC14\uD558\uC600\uB2E4.\n  \"\uB108 \uB9D0 \uB9C8\uB77C!\"\n  \"\uADF8\uB798!\"\n  \uC870\uAE08 \uC788\uB354\uB2C8 \uC694 \uC544\uB798\uC11C,\n  \"\uC810\uC21C\uC544! \uC810\uC21C\uC544! \uC774\uB144\uC774 \uBC14\uB290\uC9C8\uC744 \uD558\uB2E4 \uB9D0\uAD6C \uC5B4\uB51C \uAC14\uC5B4?\"\n  \uD558\uACE0 \uC5B4\uB51C \uAC14\uB2E4 \uC628 \uB4EF\uC2F6\uC740 \uADF8 \uC5B4\uBA38\uB2C8\uAC00 \uC5ED\uC815\uC774 \uB300\uB2E8\uD788 \uB0AC\uB2E4.\n  \uC810\uC21C\uC774\uAC00 \uAC81\uC744 \uC794\uB729 \uC9D1\uC5B4\uBA39\uACE0 \uAF43\uBC11\uC744 \uC0B4\uAE08\uC0B4\uAE08 \uAE30\uC5B4\uC11C \uC0B0 \uC544\uB798\uB85C \uB0B4\uB824\uAC04 \uB2E4\uC74C \uB098\uB294 \uBC14\uC704\uB97C \uB07C\uACE0 \uC5C9\uAE08\uC5C9\uAE08 \uAE30\uC5B4\uC11C \uC0B0\uC704\uB85C \uCE58\uBE7C\uC9C0 \uC54A\uC744 \uC218 \uC5C6\uC5C8\uB2E4.\n";
